import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MDBCol, MDBContainer, MDBRow, MDBCard, MDBCardText, MDBCardBody, MDBCardImage, MDBTypography, MDBIcon } from 'mdb-react-ui-kit';

const ArtistProfilePage = () => {
  const [artistDetails, setArtistDetails] = useState({
    name: '',
    email: '',
    username: '',
    role: '',
    image: '',
  });
  const [newImage, setNewImage] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const artistId = localStorage.getItem('artistId'); // Fetch artistId from localStorage

  // Fetch artist details on component mount
  useEffect(() => {
    if (!artistId) {
      setErrorMessage('Artist not logged in.');
      return;
    }

    // Fetch artist details from API
    axios
      .get(`http://localhost:8080/api/artists/${artistId}`)
      .then((response) => {
        setArtistDetails({
          name: response.data.name,
          email: response.data.email,
          username: response.data.username,
          role: response.data.role,
          image: response.data.image,
        });
      })
      .catch((error) => {
        setErrorMessage('Failed to fetch artist details.');
        console.error(error);
      });
  }, [artistId]);

  // Handle image change when selecting a new file
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewImage(file);
    }
  };

  // Handle profile update
  const handleUpdateProfile = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', artistDetails.name);
    formData.append('email', artistDetails.email);
    formData.append('username', artistDetails.username);
    formData.append('role', artistDetails.role);  // Ensure role is added
    if (newImage) formData.append('image', newImage);  // Add image if it's provided

    try {
      const response = await axios.put(
        `http://localhost:8080/api/artists/update/${artistId}`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',  // Ensure this is set
          },
        }
      );
      setSuccessMessage('Artist profile updated successfully!');

      // After a successful update, update the displayed image as well
      setArtistDetails((prevDetails) => ({
        ...prevDetails,
        image: `/uploads/${response.data.image}`,  // Update with the new image path
      }));
    } catch (error) {
      setErrorMessage('Failed to update profile.');
      console.error(error.response ? error.response.data : error.message);
    }
  };

  return (
    <section className="vh-100" style={{ backgroundColor: '#f4f5f7' }}>
      <MDBContainer className="py-5 h-100">
        <MDBRow className="justify-content-center align-items-center h-100">
          <MDBCol lg="6" className="mb-4 mb-lg-0">
            <MDBCard className="mb-3" style={{ borderRadius: '.5rem' }}>
              <MDBRow className="g-0">
                {/* Profile Image */}
                <MDBCol md="4" className="gradient-custom text-center text-white" style={{ borderTopLeftRadius: '.5rem', borderBottomLeftRadius: '.5rem' }}>
                  <MDBCardImage
                    src={artistDetails.image ? `http://localhost:8080${artistDetails.image}` : 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava1-bg.webp'}
                    alt="Avatar"
                    className="my-5"
                    style={{ width: '80px', height: '80px', borderRadius: '50%' }}
                    fluid
                  />
                  <MDBTypography tag="h5">{artistDetails.name}</MDBTypography>
                  <MDBCardText>{artistDetails.role}</MDBCardText>
                </MDBCol>

                {/* Profile Info */}
                <MDBCol md="8">
                  <MDBCardBody className="p-4">
                    {/* Success and Error Messages */}
                    {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
                    {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}

                    <MDBTypography tag="h6">Information</MDBTypography>
                    <hr className="mt-0 mb-4" />
                    <MDBRow className="pt-1">
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Email</MDBTypography>
                        <MDBCardText className="text-muted">{artistDetails.email}</MDBCardText>
                      </MDBCol>
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Username</MDBTypography>
                        <MDBCardText className="text-muted">{artistDetails.username}</MDBCardText>
                      </MDBCol>
                    </MDBRow>

                    <MDBTypography tag="h6">Projects</MDBTypography>
                    <hr className="mt-0 mb-4" />
                    <MDBRow className="pt-1">
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Recent</MDBTypography>
                        <MDBCardText className="text-muted">Lorem ipsum</MDBCardText>
                      </MDBCol>
                      <MDBCol size="6" className="mb-3">
                        <MDBTypography tag="h6">Most Viewed</MDBTypography>
                        <MDBCardText className="text-muted">Dolor sit amet</MDBCardText>
                      </MDBCol>
                    </MDBRow>

                    <form onSubmit={handleUpdateProfile}>
                      <div className="mb-3">
                        <label htmlFor="image">Profile Image</label>
                        <input
                          type="file"
                          id="image"
                          onChange={handleImageChange}
                          className="form-control"
                        />
                      </div>
                      <button type="submit" className="btn btn-primary">Update Profile</button>
                    </form>
                  </MDBCardBody>
                </MDBCol>
              </MDBRow>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
};

export default ArtistProfilePage;
