import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ArtworkList.css'; // You can reuse the styles

export default function CartPage({ cartItems, setCartItems }) {
  const navigate = useNavigate();

  const handleRemoveFromCart = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  const handleGoBack = () => {
    navigate('/');
  };

  return (
    <div className="cart-page">
      <h1>Your Cart</h1>
      {cartItems.length === 0 ? (
        <p>No items in the cart.</p>
      ) : (
        <div className="artwork-container">
          {cartItems.map((item) => (
            <div key={item.id} className="artwork-item">
              <img src={item.imageUrl} alt={item.artist} />
              <h2>{item.artist}</h2>
              <p>Cost: {item.cost}</p>
              <button className="cart-button" onClick={() => handleRemoveFromCart(item.id)}>Remove</button>
            </div>
          ))}
        </div>
      )}
      <button className="cart-button" onClick={handleGoBack}>Continue Shopping</button>
    </div>
  );
}
