import React, { useState, useEffect } from 'react';
import { Route, Routes, useLocation } from 'react-router-dom';
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register';
import ArtworkList from './components/ArtworkList';
import CartPage from './components/CartPage';
import AboutPage from './components/About';

import VisitorHomePage from './Visitor/VisitorHomePage';

// Example Navbars
import Navbar from './components/Navbar';

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const [userId, setUserId] = useState(null);
  const location = useLocation();

  useEffect(() => {
    // Example: Retrieve user ID from localStorage after login
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setUserId(storedUserId);
    }
  }, []);

  return (
    <div>
      <Navbar /> {/* Example Navbar */}
      <Routes>
        {/* Public Routes */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* User-Specific Routes */}
        <Route path="/" element={<Home userId={userId} />} />
        <Route path="/about" element={<AboutPage userId={userId} />} />
        {userId && (
          <>
            <Route path={`/artworks/${userId}`} element={<ArtworkList setCartItems={setCartItems} cartItems={cartItems} userId={userId} />} />
            <Route path={`/cart/${userId}`} element={<CartPage cartItems={cartItems} setCartItems={setCartItems} userId={userId} />} />

            <Route path={`/visitor/${userId}`} element={<VisitorHomePage userId={userId} />} /> {/* Visitor Page */}
          </>
        )}
      </Routes>
    </div>
  );
};

export default App;
