import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Navbar.css';

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    const Id = localStorage.getItem('userId'); // Fetch userId stored in localStorage
    setIsAuthenticated(!!token);

    if (userId) {
      axios.get(`http://localhost:8080/api/users/${Id}`)
        .then((response) => setUserData(response.data))
        .catch((error) => console.error('Error fetching user data:', error));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userId');
    setIsAuthenticated(false);
    navigate('/'); // Redirect to home after logout
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand" style={{ color: 'white' }}>Art Gallery</div>
      <div className="navbar-links">
        <Link to="/">Home</Link>
        <Link to="/artworks">Artworks</Link>
        <Link to="/cart">Cart</Link>
        <Link to="/about">About</Link>
        {isAuthenticated && userData ? (
          <>
            {/* Profile link and image */}
            <Link to="/profile">
              <img
                src={userData.profileImageUrl || '/default-profile.png'} // Fallback for missing profile image
                alt="Profile"
                className="navbar-profile-image"
              />
            </Link>
            <button
              onClick={handleLogout}
              style={{ background: 'none', border: 'none', color: 'white', cursor: 'pointer' }}
            >
              Logout
            </button>
          </>
        ) : (
          <Link to="/login">Login/Register</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
