import React, { useState } from 'react';
import axios from 'axios';
import '../styles/Login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault(); // Prevent the form from reloading the page

    const loginData = { username, password };

    try {
      // Send login request to backend
      const response = await axios.post('http://localhost:8080/api/login', loginData);

      // Extract role and ID from response
      const { role, id } = response.data;

      // Redirect user based on role
      switch (role) {
        case 'ADMIN':
          window.location.href = `/admin/${id}`;
          break;
        case 'ARTIST':
          window.location.href = `/artist/${id}`;
          break;
        case 'VISITOR':
          window.location.href = `/visitor/${id}`;
          break;
        case 'CURATOR':
          window.location.href = `/curator/${id}`;
          break;
        default:
          window.location.href = '/'; // Fallback
          break;
      }
    } catch (error) {
      // Handle error and display appropriate message
      setErrorMessage(error.response?.data?.message || 'Login failed. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleLogin}>
        <div>
          <h2 style={{ color: 'white', fontSize: '30px', textAlign: 'center' }}>Login</h2>
          <label style={{ color: 'white', fontSize: '15px' }}>Username</label>
          <input
            type="text"
            style={{ backgroundColor: 'white', color: 'black' }}
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label style={{ color: 'white', fontSize: '15px' }}>Password</label>
          <input
            type="password"
            style={{ backgroundColor: 'white', color: 'black' }}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {errorMessage && (
          <p style={{ color: 'red', fontWeight: 'bold', textAlign: 'center' }}>
            {errorMessage}
          </p>
        )}
        <div className="login-button">
          <button type="submit">
            <span style={{ fontWeight: 'bold' }}>LOGIN</span>
          </button>
        </div>
        <p style={{ marginTop: '10px', color: 'white' }}>
          Don't have an account? <a href="/register" style={{ color: 'lightblue' }}>Create one here</a>
        </p>
      </form>
    </div>
  );
};

export default Login;
