import React, { useState, useEffect } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import "chart.js/auto"; // Import chart.js for automatic chart registration
import "../styles/ArtistProfile.css"; // Ensure to create this CSS file

const ArtistProfilePage = () => {
  const [artistDetails, setArtistDetails] = useState({
    name: "",
    email: "",
    username: "",
    imagePath: "",
  });
  const [newImage, setNewImage] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const artistId = localStorage.getItem("artistId"); // Fetch artistId from localStorage

  // Hardcoded statistics
  const statistics = {
    totalArts: 25,
    orders: 15,
    accepted: 12,
    rejected: 3,
  };

  // Sales graph data (hardcoded for now)
  const salesData = {
    labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
    datasets: [
      {
        label: "Sales ($)",
        data: [1200, 1900, 800, 1500, 2000, 2500],
        backgroundColor: "rgba(54, 162, 235, 0.6)",
        borderColor: "rgba(54, 162, 235, 1)",
        borderWidth: 1,
      },
    ],
  };

  // Fetch artist details on component mount
  useEffect(() => {
    if (!artistId) {
      setErrorMessage("Artist not logged in.");
      return;
    }

    axios
      .get(`http://localhost:8080/api/artists/${artistId}`)
      .then((response) => {
        setArtistDetails(response.data);
      })
      .catch((error) => {
        setErrorMessage("Failed to fetch artist details.");
        console.error(error);
      });
  }, [artistId]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewImage(file);
    }
  };

  const handleSaveChanges = async () => {
    const formData = new FormData();
    formData.append("name", artistDetails.name);
    formData.append("email", artistDetails.email);
    formData.append("role", artistDetails.role);
    if (newImage) formData.append("image", newImage);

    try {
      const response = await axios.put(
        `http://localhost:8080/api/artists/update/${artistId}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.status === 200) {
        setSuccessMessage("Profile updated successfully!");
        setIsEditing(false);
        fetchUpdatedProfile();
      } else {
        setErrorMessage("Failed to update profile.");
      }
    } catch (error) {
      setErrorMessage("Failed to update profile.");
      console.error(error.response ? error.response.data : error.message);
    }
  };

  const fetchUpdatedProfile = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/artists/${artistId}`
      );
      setArtistDetails(response.data);
    } catch (error) {
      setErrorMessage("Failed to fetch updated profile.");
      console.error(error);
    }
  };

  return (
    <div className="dashboard-container">
      {/* Left Side Profile */}
      <div className="profile-container">
        <div className="profile-card">
          <div className="profile-header">
            <div className="profile-image-container">
              <img
                src={
                  artistDetails.imagePath
                    ? `http://localhost:8080${artistDetails.imagePath}?${new Date().getTime()}`
                    : "/default-avatar.jpg"
                }
                alt="Profile"
                className="profile-image"
              />
              {isEditing && (
                <input
                  type="file"
                  onChange={handleImageChange}
                  className="file-input"
                />
              )}
            </div>
            {/* Only show artist name, remove role */}
            <h2>{artistDetails.name}</h2>
          </div>

          <div className="profile-body">
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            {successMessage && (
              <p className="success-message">{successMessage}</p>
            )}

            <div className="info">
              <label>Name</label>
              {isEditing ? (
                <input
                  type="text"
                  value={artistDetails.name}
                  onChange={(e) =>
                    setArtistDetails({ ...artistDetails, name: e.target.value })
                  }
                  className="input-field"
                />
              ) : (
                <p>{artistDetails.name}</p>
              )}

              <label>Email</label>
              {isEditing ? (
                <input
                  type="email"
                  value={artistDetails.email}
                  onChange={(e) =>
                    setArtistDetails({
                      ...artistDetails,
                      email: e.target.value,
                    })
                  }
                  className="input-field"
                />
              ) : (
                <p>{artistDetails.email}</p>
              )}

              <label>Role</label>
              {isEditing ? (
                <input
                  type="text"
                  value={artistDetails.role}
                  onChange={(e) =>
                    setArtistDetails({
                      ...artistDetails,
                      role: e.target.value,
                    })
                  }
                  className="input-field"
                />
              ) : (
                <p>{artistDetails.role}</p>
              )}
            </div>

            {isEditing ? (
              <div className="button-group">
                <button className="save-button" onClick={handleSaveChanges}>
                  Save
                </button>
                <button
                  className="cancel-button"
                  onClick={() => setIsEditing(false)}
                >
                  Cancel
                </button>
              </div>
            ) : (
              <button
                className="edit-button"
                onClick={() => setIsEditing(true)}
              >
                Edit Profile
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Right Side Statistics */}
      <div className="statistics-container">
        <div className="statistics-card">
          <h3>Artist Statistics</h3>
          <div className="statistics-item">
            <label>Total Arts:</label>
            <p>{statistics.totalArts}</p>
          </div>
          <div className="statistics-item">
            <label>Total Orders:</label>
            <p>{statistics.orders}</p>
          </div>
          <div className="statistics-item">
            <label>Accepted Orders:</label>
            <p>{statistics.accepted}</p>
          </div>
          <div className="statistics-item">
            <label>Rejected Orders:</label>
            <p>{statistics.rejected}</p>
          </div>
        </div>

        {/* Sales Graph */}
        <div className="statistics-graph">
          <h3>Sales Overview</h3>
          <Bar data={salesData} />
        </div>
      </div>
    </div>
  );
};

export default ArtistProfilePage;
