import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import "chart.js/auto";

const CuratorProfile = () => {
  const [curator, setCurator] = useState(null);
  const [error, setError] = useState("");
  const [statistics, setStatistics] = useState({
    totalExhibitions: 0,
    placesVisited: [],
  });

  const curatorId = localStorage.getItem("curatorId"); // Fetch curator ID from localStorage

  // Fetch curator profile and statistics on component mount
  useEffect(() => {
    if (!curatorId) {
      setError("Curator ID not found. Please log in.");
      return;
    }

    // Fetch curator profile
    axios
      .get(`http://localhost:8080/api/curators/${curatorId}`)
      .then((response) => setCurator(response.data))
      .catch((err) => {
        setError("Failed to fetch curator profile.");
        console.error(err);
      });

    // Fetch statistics (total exhibitions and places visited)
    axios
      .get(`http://localhost:8080/api/curators/${curatorId}/statistics`)
      .then((response) => setStatistics(response.data))
      .catch((err) => {
        setError("Failed to fetch statistics.");
        console.error(err);
      });
  }, [curatorId]);

  if (error) {
    return (
      <div style={styles.error}>
        <p>{error}</p>
      </div>
    );
  }

  if (!curator) {
    return (
      <div style={styles.loading}>
        <p>Loading curator profile...</p>
      </div>
    );
  }

  // Prepare data for the bar chart
  const barChartData = {
    labels: statistics.placesVisited.map((place) => place.name),
    datasets: [
      {
        label: "Exhibitions per Place",
        data: statistics.placesVisited.map((place) => place.count),
        backgroundColor: "rgba(54, 162, 235, 0.6)",
        borderColor: "rgba(54, 162, 235, 1)",
        borderWidth: 1,
      },
    ],
  };

  return (
    <div style={styles.dashboardContainer}>
      {/* Left Side: Curator Profile */}
      <div style={styles.profileContainer}>
        <div style={styles.profileCard}>
          <h1 style={styles.heading}>Curator Profile</h1>
          <p>
            <strong>Name:</strong> {curator.name}
          </p>
          <p>
            <strong>Email:</strong> {curator.email}
          </p>
          <p>
            <strong>Phone:</strong> {curator.phone}
          </p>
          <p>
            <strong>Bio:</strong> {curator.bio}
          </p>
          <p>
            <strong>Joined:</strong> {curator.joinedDate}
          </p>
        </div>
      </div>

      {/* Right Side: Statistics */}
      <div style={styles.statisticsContainer}>
        <div style={styles.statisticsCard}>
          <h3>Total Exhibitions</h3>
          <p style={styles.statisticsValue}>{statistics.totalExhibitions}</p>
        </div>
        <div style={styles.statisticsCard}>
          <h3>Places Visited</h3>
          {statistics.placesVisited.length === 0 ? (
            <p>No places visited yet.</p>
          ) : (
            <ul style={styles.placesList}>
              {statistics.placesVisited.map((place, index) => (
                <li key={index}>
                  {place.name} - {place.count} Exhibitions
                </li>
              ))}
            </ul>
          )}
        </div>
        <div style={styles.statisticsGraph}>
          <h3>Exhibitions per Place</h3>
          <Bar data={barChartData} />
        </div>
      </div>
    </div>
  );
};

const styles = {
  dashboardContainer: {
    display: "flex",
    justifyContent: "space-between",
    padding: "20px",
    fontFamily: "'Roboto', sans-serif",
  },
  profileContainer: {
    width: "40%",
    padding: "20px",
  },
  profileCard: {
    backgroundColor: "#f9f9f9",
    padding: "20px",
    borderRadius: "8px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    lineHeight: "1.6",
  },
  heading: {
    fontSize: "24px",
    marginBottom: "15px",
    color: "#333",
  },
  statisticsContainer: {
    width: "55%",
    padding: "20px",
  },
  statisticsCard: {
    backgroundColor: "#f9f9f9",
    padding: "15px",
    borderRadius: "8px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    marginBottom: "20px",
  },
  statisticsValue: {
    fontSize: "32px",
    fontWeight: "bold",
    color: "#007bff",
    textAlign: "center",
  },
  placesList: {
    listStyle: "none",
    padding: 0,
  },
  statisticsGraph: {
    backgroundColor: "#fff",
    padding: "15px",
    borderRadius: "8px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  error: {
    color: "red",
    textAlign: "center",
    marginTop: "20px",
  },
  loading: {
    textAlign: "center",
    marginTop: "20px",
  },
};

export default CuratorProfile;
