import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import "chart.js/auto"; // Import chart.js for automatic chart registration
import "../styles/ArtistProfile.css"; // Ensure to create this CSS file

const ArtistProfilePage = () => {
  const [artistDetails, setArtistDetails] = useState({
    name: "Ravi Varma",
    email: "varma@gmail.com",
    username: "ravivarma",
    role: "ARTIST",
    imagePath: "/uploads/default-avatar.jpg",
  });
  const [newImage, setNewImage] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  // Hardcoded statistics
  const statistics = {
    totalArts: 25,
    orders: 15,
    accepted: 12,
    rejected: 3,
  };

  // Sales graph data (hardcoded for now)
  const salesData = {
    labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
    datasets: [
      {
        label: "Sales ($)",
        data: [1200, 1900, 800, 1500, 2000, 2500],
        backgroundColor: "rgba(54, 162, 235, 0.6)",
        borderColor: "rgba(54, 162, 235, 1)",
        borderWidth: 1,
      },
    ],
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewImage(file);
    }
  };

  const handleSaveChanges = () => {
    setSuccessMessage("Profile updated successfully!");
    setIsEditing(false);
  };

  return (
    <div className="dashboard-container">
      {/* Left Side Profile */}
      <div className="profile-container">
        <div className="profile-card">
          <div className="profile-header">
            <div className="profile-image-container">
              <img
                src={
                  artistDetails.imagePath
                    ? `http://localhost:8080${artistDetails.imagePath}`
                    : "/default-avatar.jpg"
                }
                alt="Profile"
                className="profile-image"
              />
              {isEditing && (
                <input
                  type="file"
                  onChange={handleImageChange}
                  className="file-input"
                />
              )}
            </div>
            <h2>{artistDetails.name}</h2>
            <p>{artistDetails.role}</p>
          </div>

          <div className="profile-body">
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            {successMessage && (
              <p className="success-message">{successMessage}</p>
            )}

            <div className="info">
              <label>Name</label>
              {isEditing ? (
                <input
                  type="text"
                  value={artistDetails.name}
                  onChange={(e) =>
                    setArtistDetails({ ...artistDetails, name: e.target.value })
                  }
                  className="input-field"
                />
              ) : (
                <p>{artistDetails.name}</p>
              )}

              <label>Email</label>
              {isEditing ? (
                <input
                  type="email"
                  value={artistDetails.email}
                  onChange={(e) =>
                    setArtistDetails({
                      ...artistDetails,
                      email: e.target.value,
                    })
                  }
                  className="input-field"
                />
              ) : (
                <p>{artistDetails.email}</p>
              )}

              <label>Role</label>
              {isEditing ? (
                <input
                  type="text"
                  value={artistDetails.role}
                  onChange={(e) =>
                    setArtistDetails({
                      ...artistDetails,
                      role: e.target.value,
                    })
                  }
                  className="input-field"
                />
              ) : (
                <p>{artistDetails.role}</p>
              )}
            </div>

            {isEditing ? (
              <div className="button-group">
                <button className="save-button" onClick={handleSaveChanges}>
                  Save
                </button>
                <button
                  className="cancel-button"
                  onClick={() => setIsEditing(false)}
                >
                  Cancel
                </button>
              </div>
            ) : (
              <button
                className="edit-button"
                onClick={() => setIsEditing(true)}
              >
                Edit Profile
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Right Side Statistics */}
      <div className="statistics-container">
        <div className="statistics-card">
          <h3>Artist Statistics</h3>
          <div className="statistics-item">
            <label>Total Arts:</label>
            <p>{statistics.totalArts}</p>
          </div>
          <div className="statistics-item">
            <label>Total Orders:</label>
            <p>{statistics.orders}</p>
          </div>
          <div className="statistics-item">
            <label>Accepted Orders:</label>
            <p>{statistics.accepted}</p>
          </div>
          <div className="statistics-item">
            <label>Rejected Orders:</label>
            <p>{statistics.rejected}</p>
          </div>
        </div>

        {/* Sales Graph */}
        <div className="statistics-graph">
          <h3>Sales Overview</h3>
          <Bar data={salesData} />
        </div>
      </div>
    </div>
  );
};

export default ArtistProfilePage;
