import React, { useState } from 'react';
import axios from 'axios';
import '../styles/Login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const handleLogin = async (e) => {
    e.preventDefault();
  
    if (!role) {
      setErrorMessage('Please select a role.');
      return;
    }
  
    const loginData = { username, password, role };
    let loginUrl = '';
  
    // Determine the login URL based on the selected role
    switch (role) {
      case 'ADMIN':
        loginUrl = 'http://localhost:8080/api/admin/login';
        break;
      case 'ARTIST':
        loginUrl = 'http://localhost:8080/api/artists/login';
        break;
      case 'CURATOR':
        loginUrl = 'http://localhost:8080/api/curators/login';
        break;
      case 'VISITOR':
        loginUrl = 'http://localhost:8080/api/visitors/login';
        break;
      default:
        setErrorMessage('Invalid role');
        return;
    }
  
    try {
      const response = await axios.post(loginUrl, loginData);
      console.log(response.data);  // Log response for debugging
  
      const { role: responseRole, userId } = response.data;
  
      if (userId) {
        // Store userId and role in localStorage for all roles
        localStorage.setItem('authToken', 'valid-auth-token'); // Mock token for authentication
        localStorage.setItem('userId', userId);
        localStorage.setItem('role', responseRole);
  
        // Redirect user based on role
        switch (responseRole) {
          case 'ADMIN':
            window.location.href = `/admin/${userId}`;
            break;
          case 'ARTIST':
            window.location.href = `/artist/${userId}`;
            break;
          case 'VISITOR':
            window.location.href = `/${userId}`;
            break;
          case 'CURATOR':
            window.location.href = `/curator/${userId}`;
            break;
          default:
            window.location.href = '/login';
            break;
        }
      } else {
        setErrorMessage('Invalid response, userId is missing');
      }
    } catch (error) {
      setErrorMessage(error.response?.data?.message || 'Login failed. Please try again.');
    }
  };
  
  
  return (
    <div className="login-container">
      <form onSubmit={handleLogin}>
        <div>
          <h2 style={{ color: 'white', fontSize: '30px', textAlign: 'center' }}>Login</h2>
          <label style={{ color: 'white', fontSize: '15px' }}>Username</label>
          <input
            type="text"
            style={{ backgroundColor: 'white', color: 'black' }}
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label style={{ color: 'white', fontSize: '15px' }}>Password</label>
          <input
            type="password"
            style={{ backgroundColor: 'white', color: 'black' }}
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label style={{ color: 'white', fontSize: '15px' }}>Role</label>
          <select
            style={{ backgroundColor: 'white', color: 'black' }}
            value={role}
            onChange={(e) => setRole(e.target.value)}
            required
          >
            <option value="" disabled>
              Select Role
            </option>
            <option value="ADMIN">Admin</option>
            <option value="ARTIST">Artist</option>
            <option value="VISITOR">Visitor</option>
            <option value="CURATOR">Curator</option>
          </select>
        </div>
        {errorMessage && (
          <p style={{ color: 'red', fontWeight: 'bold', textAlign: 'center' }}>
            {errorMessage}
          </p>
        )}
        <div className="login-button">
          <button type="submit">
            <span style={{ fontWeight: 'bold' }}>LOGIN</span>
          </button>
        </div>
        <p style={{ marginTop: '10px', color: 'white' }}>
          Don't have an account?{' '}
          <a href="/register" style={{ color: 'lightblue' }}>
            Create one here
          </a>
        </p>
      </form>
    </div>
  );
};

export default Login;
