import React, { useEffect, useState } from 'react';
import ArtworkService from './ArtworkService';

const ArtworkList = () => {
  const [artworks, setArtworks] = useState([]);
  const [hoveredCard, setHoveredCard] = useState(null); // State to track the hovered card

  useEffect(() => {
    ArtworkService.getAllArtworks()
      .then((response) => {
        setArtworks(response.data);
      })
      .catch((error) => {
        console.error('Error fetching artworks:', error);
      });
  }, []);

  return (
    <div className="bg-background text-primary-foreground min-h-screen flex flex-col items-center justify-center">
      <h1 className="text-3xl font-bold mb-8">Artwork List</h1>
      <div className="max-w-4xl grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 p-4">
        {artworks.map((artwork) => (
          <div
            className="bg-card shadow-lg rounded-lg overflow-hidden flex flex-col relative"
            key={artwork.id}
            onMouseEnter={() => setHoveredCard(artwork.id)} // Set hovered card id
            onMouseLeave={() => setHoveredCard(null)} // Reset hovered card
          >
            <img src={artwork.imageUrl} alt={artwork.title} className="w-full h-48 object-cover" />
            <div className="p-4 flex flex-col justify-between flex-1">
              <div>
                <h3 className="text-lg font-semibold">{artwork.title}</h3>
                {/* Adjust opacity based on hover state */}
                <p className={`text-sm text-muted-foreground ${hoveredCard === artwork.id ? 'opacity-100' : 'opacity-0'} artist-name transition-opacity duration-200`}>
                  by {artwork.artist}
                </p>
                <p className="text-base mt-2">Price: ${artwork.price}</p>
              </div>
              <div className="flex justify-between items-center mt-4">
                <button className="bg-primary text-primary-foreground px-4 py-2 rounded-lg">Add to Cart</button>
                <div className="flex items-center">
                  <button className="bg-secondary text-secondary-foreground px-2 py-1 rounded-l-lg">-</button>
                  <span className="bg-input text-primary-foreground px-4 py-2">1</span>
                  <button className="bg-secondary text-secondary-foreground px-2 py-1 rounded-r-lg">+</button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ArtworkList;
