// Home.js

import React, { useEffect, useState } from 'react';
import '../styles/Home.css'; // Import the CSS file

const Home = () => {
    const [current, setCurrent] = useState(0);
    const items = [
        'static/art2.jpg',
        'frontend/src/static/art1.jpg',
        'frontend/src/static/art3.jpg',
    ];

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrent((prev) => (prev + 1) % items.length);
        }, 3000); // Change every 3 seconds
        return () => clearInterval(interval);
    }, [items.length]);

    return (
        <div>
            {/* Navbar */}
            <nav className="navbar">
                <div className="navbar-brand">Art Gallery</div>
                <div className="navbar-links">
                    <a href="/">Home</a>
                    <a href="/artworks">Artworks</a>
                    <a href="/add-artwork">Add Artwork</a>
                    <a href="/login">Login</a>
                    <a href="/register">Register</a>
                    
                </div>
            </nav>

            {/* Carousel */}
            <div className="carousel">
                {items.map((item, index) => (
                    <div
                        className="carousel-item"
                        style={{ opacity: current === index ? 1 : 0 }}
                        key={index}
                    >
                        <img src={item} alt={`Artwork ${index + 1}`} />
                    </div>
                ))}
            </div>
            <h1>Welcome to the Art Gallery</h1>
            <p>Please Login or Register to view the artworks.</p>
        </div>
    );
};

export default Home;
