import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Navbar.css';

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userId, setUserId] = useState(null); // Store userId
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    const storedUserId = localStorage.getItem('userId'); // Fetch userId from localStorage
    setIsAuthenticated(!!token); // Check if user is authenticated
    setUserId(storedUserId); // Set userId from localStorage
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken'); // Remove auth token
    localStorage.removeItem('userId'); // Remove userId
    setIsAuthenticated(false); // Update state
    setUserId(null); // Clear userId
    navigate('/'); // Redirect to home
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand" style={{ color: 'white' }}>Art Gallery</div>
      <div className="navbar-links">
        <Link to="/">Home</Link>
        <Link to="/artworks">Artworks</Link>
        <Link to="/cart">Cart</Link>
        <Link to="/about">About</Link>
        {isAuthenticated && userId ? (
          <>
            {/* Profile link */}
            <Link to={`/visitor/${userId}/profile`}>
              <img
                src="/default-profile.png" // Replace with profile image logic if available
                alt="Profile"
                className="navbar-profile-image"
              />
            </Link>
            <button
              onClick={handleLogout}
              style={{ background: 'none', border: 'none', color: 'white', cursor: 'pointer' }}
            >
              Logout
            </button>
          </>
        ) : (
          <Link to="/login">Login/Register</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
