import React, { useState } from 'react';
import { Route, Routes, useLocation } from 'react-router-dom';
import Home from './components/Home';
import Login from './components/Login';
import Register from './components/Register';
import ArtworkList from './components/ArtworkList';
import AdminHomePage from './Admin/AdminHomePage';
import ArtistHomePage from './Artist/ArtistHomePage';
import CuratorHomePage from './Curator/CuratorHomePage';
import VisitorHomePage from './Visitor/VisitorHomePage';
import CartPage from './components/CartPage';
import AboutPage from './components/About';

// Import Navbars
import Navbar from './components/Navbar';
import AdminNavbar from './components/AdminNavbar';
import ArtistNavbar from './components/ArtistNavbar';
import CuratorNavbar from './components/CuratorNavbar';

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const location = useLocation();

  const getNavbar = () => {
    if (location.pathname.startsWith('/admin')) return <AdminNavbar />;
    if (location.pathname.startsWith('/artist')) return <ArtistNavbar />;
    if (location.pathname.startsWith('/curator')) return <CuratorNavbar />;
    return <Navbar />; // Default to VisitorNavbar
  };

  return (
    <div>
      {getNavbar()} {/* Display the appropriate navbar */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<AboutPage />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/artworks" element={<ArtworkList setCartItems={setCartItems} cartItems={cartItems} />} />
        <Route path="/cart" element={<CartPage cartItems={cartItems} setCartItems={setCartItems} />} />
        <Route path="/admin/*" element={<AdminHomePage />}>
          <Route path="manage-users" element={<ManageUs />} />
          <Route path="manage-artworks" element={<ManageArtworks />} />
          <Route path="site-settings" element={<SiteSettings />} />
        </Route>
        <Route path="/artist" element={<ArtistHomePage />} />
        <Route path="/curator" element={<CuratorHomePage />} />
        <Route path="/visitor" element={<VisitorHomePage />} />
      </Routes>
      <p style={{ textAlign: 'center' }}>&copy; {new Date().getFullYear()} Art Gallery. All rights reserved.</p>
    </div>
  );
};

export default App;
