import React from 'react';
import img1 from "../static/god2.jpg";

const cardClass = 'bg-card shadow-lg rounded-lg overflow-hidden flex flex-col relative';
const primaryBtnClass = 'bg-primary text-primary-foreground px-4 py-2 rounded-lg';
const secondaryBtnClass = 'bg-secondary text-secondary-foreground px-2 py-1';
const inputClass = 'bg-input text-primary-foreground px-4 py-2';

const ArtworkCard = ({ imageUrl, title, artist, price, quantity, onQuantityChange }) => {
  const handleMouseEnter = (e) => {
    const artistName = e.currentTarget.querySelector('.artist-name');
    if (artistName) {
      artistName.style.opacity = '1';
    }
  };

  const handleMouseLeave = (e) => {
    const artistName = e.currentTarget.querySelector('.artist-name');
    if (artistName) {
      artistName.style.opacity = '0';
    }
  };

  return (
    <div className={cardClass} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <img src={imageUrl} alt={title} className="w-full h-48 object-cover" />
      <div className="p-4 flex flex-col justify-between flex-1">
        <div>
          <h3 className="text-lg font-semibold">{title}</h3>
          <p className="text-sm text-muted-foreground opacity-0 artist-name">by {artist}</p>
          <p className="text-base mt-2">Price: ${price}</p>
        </div>
        <div className="flex justify-between items-center mt-4">
          <button className={primaryBtnClass}>Add to Cart</button>
          <div className="flex items-center">
            <button className={`${secondaryBtnClass} rounded-l-lg`} onClick={() => onQuantityChange(-1)}>-</button>
            <span className={inputClass}>{quantity}</span>
            <button className={`${secondaryBtnClass} rounded-r-lg`} onClick={() => onQuantityChange(1)}>+</button>
          </div>
        </div>
      </div>
    </div>
  );
};

const ArtworkList = () => {
  const artworks = [
    { id: 1, title: 'Artwork Title', artist: 'Artist Name', price: 100, img: img1 },
    { id: 2, title: 'Artwork Title 2', artist: 'Artist Name 2', price: 150, img: 'https://placehold.co/301' },
    { id: 3, title: 'Artwork Title 3', artist: 'Artist Name 3', price: 120, img: 'https://placehold.co/302' },
  ];

  const [quantities, setQuantities] = useState(artworks.map(() => 1)); // Initialize quantities

  const handleQuantityChange = (index, increment) => {
    setQuantities(prev => {
      const newQuantities = [...prev];
      newQuantities[index] = Math.max(1, newQuantities[index] + increment); // Ensure quantity is at least 1
      return newQuantities;
    });
  };

  return (
    <div className="bg-background text-primary-foreground min-h-screen flex flex-col items-center justify-center">
      <div className="max-w-4xl grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 p-4">
        {artworks.map((artwork, index) => (
          <ArtworkCard 
            key={artwork.id}
            imageUrl={artwork.img}
            title={artwork.title}
            artist={artwork.artist}
            price={artwork.price}
            quantity={quantities[index]}
            onQuantityChange={(increment) => handleQuantityChange(index, increment)}
          />
        ))}
      </div>
    </div>
  );
};

export default ArtworkList;
