import React, { useState } from 'react';
import '../styles/AddArtwork.css'; // Optional: Style this page as needed

export default function AddArtwork() {
  const [artist, setArtist] = useState('');
  const [cost, setCost] = useState('');
  const [description, setDescription] = useState(''); // New state for description
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!artist || !cost || !description || !image) {
      setMessage('All fields are required');
      return;
    }

    const formData = new FormData();
    formData.append('artist', artist);
    formData.append('cost', cost);
    formData.append('description', description); // Add description to form data
    formData.append('image', image);

    try {
      const response = await fetch('/api/artworks', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        setMessage('Artwork added successfully!');
        setArtist('');
        setCost('');
        setDescription(''); // Reset description field
        setImage(null);
      } else {
        setMessage('Failed to add artwork. Try again.');
      }
    } catch (error) {
      console.error('Error adding artwork:', error);
      setMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div className="add-artwork">
      <h1>Add New Artwork</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="artist">Artist Name:</label>
          <input
            type="text"
            id="artist"
            value={artist}
            onChange={(e) => setArtist(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="cost">Cost:</label>
          <input
            type="number"
            id="cost"
            value={cost}
            onChange={(e) => setCost(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            rows="4"
            required
          />
        </div>
        <div>
          <label htmlFor="image">Upload Image:</label>
          <input
            type="file"
            id="image"
            accept="image/*"
            onChange={(e) => setImage(e.target.files[0])}
            required
          />
        </div>
        <button type="submit">Add Artwork</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
}
