import React, { useEffect, useState } from 'react';
import '../styles/Home.css'; // Import the CSS file

const Home = () => {
    const [current, setCurrent] = useState(0);
    const items = [
        'path/to/your/image1.jpg', // Replace with your image paths
        'path/to/your/image2.jpg',
        'path/to/your/image3.jpg',
    ];

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrent((prev) => (prev + 1) % items.length);
        }, 3000); // Change every 3 seconds
        return () => clearInterval(interval);
    }, [items.length]);

    const toggleTheme = () => {
        document.body.classList.toggle('dark-theme');
    };

    return (
        <div>
            {/* Navbar */}
            <nav className="navbar">
                <div className="navbar-brand">Art Gallery</div>
                <div className="navbar-links">
                    <a href="/">Home</a>
                    <a href="/artworks">Artworks</a>
                    <a href="/add-artwork">Add Artwork</a>
                    <a href="/login">Login</a>
                    <a href="/register">Register</a>
                    <input type="checkbox" id="toggle_checkbox" onChange={toggleTheme} />
                    <label htmlFor="toggle_checkbox">
                        <span className="star">ðŸŒŸ</span>
                        <span className="moon">ðŸŒ™</span>
                    </label>
                </div>
            </nav>

            {/* Carousel */}
            <div className="carousel">
                {items.map((item, index) => (
                    <div
                        className="carousel-item"
                        style={{ opacity: current === index ? 1 : 0 }}
                        key={index}
                    >
                        <img src={item} alt={`Artwork ${index + 1}`} />
                    </div>
                ))}
            </div>
            <h1>Welcome to the Art Gallery</h1>
            <p>Please Login or Register to view the artworks.</p>
        </div>
    );
};

export default Home;
