import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const ArtistNavbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userId, setUserId] = useState(null); // Store userId from localStorage
  const [role, setRole] = useState(null); // Store role from localStorage
  const navigate = useNavigate();

  // Fetch user data from localStorage on mount
  useEffect(() => {
    const token = localStorage.getItem('authToken'); // Check for authentication token
    const storedUserId = localStorage.getItem('userId'); // Fetch userId from localStorage
    const storedRole = localStorage.getItem('role'); // Fetch role from localStorage
    setIsAuthenticated(!!token); // Set authentication state
    setUserId(storedUserId); // Set userId
    setRole(storedRole); // Set role
  }, []);

  // Handle logout and clear user data from localStorage
  const handleLogout = () => {
    localStorage.removeItem('authToken'); // Remove auth token
    localStorage.removeItem('userId'); // Remove userId
    localStorage.removeItem('role'); // Remove role
    setIsAuthenticated(false); // Update state
    setUserId(null); // Clear userId
    setRole(null); // Clear role
    navigate('/login'); // Redirect to login page after logout
  };

  // If not authenticated or userId is not available, show login prompt
  if (!isAuthenticated || !userId) {
    return (
      <div style={{ color: 'white', textAlign: 'center' }}>
        Please log in to access the artist dashboard.
      </div>
    );
  }

  return (
    <nav style={styles.navbar}>
      <ul style={styles.navList}>
        <li style={styles.navItem}>
          <Link to={`/artist/${userId}`} style={styles.navLink}>Dashboard</Link>
        </li>
        <li style={styles.navItem}>
          <Link to={`/artist/${userId}/myartworks`} style={styles.navLink}>My Artworks</Link>
        </li>
        <li style={styles.navItem}>
          <Link to={`/artist/${userId}/addartwork`} style={styles.navLink}>Upload Artwork</Link>
        </li>
        <li style={styles.navItem}>
          <Link to={`/artist/${userId}/profile`} style={styles.navLink}>Profile</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="#" onClick={handleLogout} style={styles.navLink}>Logout</Link>
        </li>
      </ul>
    </nav>
  );
};

const styles = {
  navbar: {
    backgroundColor: '#333',
    padding: '15px',
    borderBottom: '2px solid #444',
  },
  navList: {
    listStyle: 'none',
    display: 'flex',
    justifyContent: 'space-around',
    margin: 0,
    padding: 0,
  },
  navItem: {
    margin: '0 10px',
  },
  navLink: {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '16px',
    fontWeight: '500',
    transition: 'color 0.3s, transform 0.2s',
  },
  navLinkHover: {
    color: '#ffcc00',
  }
};

export default ArtistNavbar;
