import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ArtworkList.css';

export default function ArtworkList({ setCartItems, cartItems }) {
  const [artworks, setArtworks] = useState([]); // State for storing artworks
  const [addedItems, setAddedItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchArtworks();
  }, []);

  useEffect(() => {
    setAddedItems(cartItems.map(item => item.id));
  }, [cartItems]);

  // Fetch artworks from the backend
  const fetchArtworks = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/artworks'); // Replace with your backend API endpoint
      if (response.ok) {
        const data = await response.json();
        setArtworks(data);
      } else {
        console.error('Failed to fetch artworks');
      }
    } catch (error) {
      console.error('Error fetching artworks:', error);
    }
  };

  // Handle adding an item to the cart
  const handleAddToCart = (artwork) => {
    if (!addedItems.includes(artwork.id)) {
      const newCartItems = [...cartItems, artwork];
      setAddedItems([...addedItems, artwork.id]);
      setCartItems(newCartItems);
    }
  };

  // Navigate to the cart page
  const handleGoToCart = () => {
    navigate('/cart');
  };

  return (
    <div className="artwork-list">
      <h1>Artworks</h1>
      <div className="artwork-grid">
        {artworks.map((artwork) => (
          <div key={artwork.id} className="artwork-card">
            <div className="artwork-image-container">
              <img 
                src={`http://localhost:8080${artwork.imagePath}`} 
                alt={artwork.artist} 
                className="artwork-image"
              />
              <div className="artwork-description">
                <p>{artwork.description}</p>
              </div>
            </div>
            <h2>{artwork.artist}</h2>
            <p className="artwork-cost">Cost: {artwork.cost}</p>
            {addedItems.includes(artwork.id) ? (
              <button className="cart-button" onClick={handleGoToCart}>Go to Cart</button>
            ) : (
              <button className="cart-button" onClick={() => handleAddToCart(artwork)}>Add to Cart</button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
