import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ArtworkList.css';

export default function ArtworkList({ setCartItems, cartItems = [] }) {
  const [artworks, setArtworks] = useState([]);
  const [addedItems, setAddedItems] = useState([]);
  const [userId, setUserId] = useState(null); // Store userId from localStorage
  const navigate = useNavigate();

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId'); // Get userId from localStorage
    setUserId(storedUserId); // Set userId in state
    fetchArtworks();
  }, []);

  // Directly calculate addedItems when needed
  const calculateAddedItems = () => {
    return cartItems.map(item => item.id);
  };

  const fetchArtworks = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/artworks');
      if (response.ok) {
        const data = await response.json();
        setArtworks(data);
      } else {
        console.error('Failed to fetch artworks');
      }
    } catch (error) {
      console.error('Error fetching artworks:', error);
    }
  };

  const handleAddToCart = (artwork) => {
    // Check if setCartItems is a function and cartItems is defined
    if (typeof setCartItems === 'function' && Array.isArray(cartItems)) {
      if (!addedItems.includes(artwork.id)) {
        const newCartItems = [...cartItems, artwork];
        setCartItems(newCartItems);
        setAddedItems(calculateAddedItems(newCartItems)); // Calculate addedItems based on the updated cartItems
      }
    } else {
      console.error('setCartItems is not a function or cartItems is not an array');
    }
  };

  const handleGoToCart = () => {
    // Ensure userId is available and redirect to the cart page
    if (userId) {
      navigate(`/visitor/${userId}/cart`);
    } else {
      console.error('User ID is not found');
      // Optionally, redirect to login page if userId is not found
      navigate('/login');
    }
  };

  return (
    <div className="artwork-list">
      <h1>Artworks</h1>
      <div className="artwork-grid">
        {artworks.map((artwork) => (
          <div key={artwork.id} className="artwork-card">
            <div className="artwork-image-container">
              <img
                src={`http://localhost:8080${artwork.imagePath}`}
                alt={artwork.title}
                className="artwork-image"
              />
            </div>
            <h2 className="artwork-title">{artwork.title}</h2>
            <div className="artwork-hover-details">
              <p className="artwork-artist">By: {artwork.artist}</p>
              <p className="artwork-description">{artwork.description}</p>
            </div>
            <p className="artwork-cost">Cost: {artwork.cost}</p>
            {addedItems.includes(artwork.id) ? (
              <button className="cart-button" onClick={handleGoToCart}>
                Go to Cart
              </button>
            ) : (
              <button className="cart-button" onClick={() => handleAddToCart(artwork)}>
                Add to Cart
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
