import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ArtistProfilePage = () => {
  const [artistDetails, setArtistDetails] = useState({
    name: '',
    email: '',
    username: '',
    role: '',
    image: '',
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  // Fetch artistId from localStorage
  const artistId = localStorage.getItem('artistId');
  console.log("Artist ID from localStorage:", artistId);

  // Fetch artist details on component mount
  useEffect(() => {
    if (!artistId) {
      setErrorMessage('Artist not logged in.');
      return;
    }

    console.log("Fetching artist details...");
    axios.get(`http://localhost:8080/api/artists/${artistId}`)
      .then(response => {
        console.log("Artist details:", response.data);
        setArtistDetails(response.data);
      })
      .catch(error => {
        console.error("Error fetching artist details:", error);
        setErrorMessage('Failed to fetch artist details.');
      });
  }, [artistId]);

  // Displaying the artist profile page
  return (
    <div className="artist-profile-container">
      <h2>Artist Profile</h2>

      {/* Error or success messages */}
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}

      {/* Profile Image */}
      <div className="profile-image-container">
        {artistDetails.image ? (
          <img
            src={`http://localhost:8080${artistDetails.image}`}
            alt="Artist Profile"
            className="profile-image"
          />
        ) : (
          <p>No image available</p>
        )}
      </div>

      <div className="artist-details">
        <div>
          <strong>Name: </strong>{artistDetails.name}
        </div>
        <div>
          <strong>Email: </strong>{artistDetails.email}
        </div>
        <div>
          <strong>Username: </strong>{artistDetails.username}
        </div>
        <div>
          <strong>Role: </strong>{artistDetails.role}
        </div>
      </div>

      {/* You can add additional features like editing the profile */}
      <button onClick={() => setSuccessMessage("Profile update functionality to be implemented.")}>
        Edit Profile
      </button>
    </div>
  );
};

export default ArtistProfilePage;
