import React, { useState } from 'react';

const ArtworkInsights = () => {
  const [insight, setInsight] = useState('');
  const [artwork, setArtwork] = useState('Select Artwork');

  const handleSave = () => {
    console.log('Insight saved for artwork:', artwork);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ fontSize: '24px', marginBottom: '15px' }}>Artwork Insights</h1>
      <label style={{ display: 'block', margin: '10px 0 5px', fontWeight: 'bold' }}>Select Artwork</label>
      <select 
        value={artwork} 
        onChange={(e) => setArtwork(e.target.value)} 
        style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ccc', borderRadius: '4px' }}
      >
        <option>Select Artwork</option>
        {/* Options here would be dynamic */}
      </select>

      <label style={{ display: 'block', margin: '10px 0 5px', fontWeight: 'bold' }}>Insight</label>
      <textarea 
        value={insight} 
        onChange={(e) => setInsight(e.target.value)} 
        style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ccc', borderRadius: '4px' }}
      />

      <button 
        onClick={handleSave} 
        style={{ padding: '10px 15px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
      >
        Save Insight
      </button>
    </div>
  );
};

export default ArtworkInsights;
