import React, { useState, useEffect } from "react";
import axios from "axios";

const CuratorProfilePage = () => {
  const [curatorDetails, setCuratorDetails] = useState({
    name: "",
    email: "",
    phone: "",
    bio: "",
    joinedDate: "",
    imagePath: "",
  });
  const [isEditing, setIsEditing] = useState(false);
  const [newImage, setNewImage] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const curatorId = localStorage.getItem("curatorId"); // Fetch curatorId from localStorage

  // Fetch curator details on component mount
  useEffect(() => {
    if (!curatorId) {
      setErrorMessage("Curator not logged in.");
      return;
    }

    axios
      .get(`http://localhost:8080/api/curators/${curatorId}`)
      .then((response) => {
        setCuratorDetails(response.data);
      })
      .catch((error) => {
        setErrorMessage("Failed to fetch curator details.");
        console.error(error);
      });
  }, [curatorId]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewImage(file);
    }
  };

  const handleSaveChanges = async () => {
    const formData = new FormData();
    formData.append("name", curatorDetails.name);
    formData.append("email", curatorDetails.email);
    formData.append("phone", curatorDetails.phone);
    formData.append("bio", curatorDetails.bio);
    if (newImage) formData.append("image", newImage);

    try {
      const response = await axios.put(
        `http://localhost:8080/api/curators/update/${curatorId}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.status === 200) {
        setSuccessMessage("Profile updated successfully!");
        setIsEditing(false);
        // Update the state with the new profile details
        setCuratorDetails((prevDetails) => ({
          ...prevDetails,
          ...response.data,
        }));
        setNewImage(null); // Clear the new image state
      } else {
        throw new Error("Failed to update profile.");
      }
    } catch (error) {
      setErrorMessage(
        error.response?.data?.message || "Failed to update profile."
      );
      console.error(error.response ? error.response.data : error.message);
    }
  };

  return (
    <div className="dashboard-container">
      {/* Left Side Profile */}
      <div className="profile-container">
        <div className="profile-card">
          <div className="profile-header">
            <div className="profile-image-container">
              <img
                src={
                  curatorDetails.imagePath
                    ? `http://localhost:8080${curatorDetails.imagePath}?${new Date().getTime()}`
                    : "/default-avatar.jpg"
                }
                alt="Profile"
                className="profile-image"
              />
              {isEditing && (
                <input
                  type="file"
                  onChange={handleImageChange}
                  className="file-input"
                />
              )}
            </div>
          </div>

          <div className="profile-body">
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            {successMessage && (
              <p className="success-message">{successMessage}</p>
            )}

            <div className="info">
              <label>Name</label>
              {isEditing ? (
                <input
                  type="text"
                  value={curatorDetails.name}
                  onChange={(e) =>
                    setCuratorDetails({
                      ...curatorDetails,
                      name: e.target.value,
                    })
                  }
                  className="input-field"
                />
              ) : (
                <p>{curatorDetails.name}</p>
              )}

              <label>Email</label>
              {isEditing ? (
                <input
                  type="email"
                  value={curatorDetails.email}
                  onChange={(e) =>
                    setCuratorDetails({
                      ...curatorDetails,
                      email: e.target.value,
                    })
                  }
                  className="input-field"
                />
              ) : (
                <p>{curatorDetails.email}</p>
              )}

              <label>Phone</label>
              {isEditing ? (
                <input
                  type="text"
                  value={curatorDetails.phone}
                  onChange={(e) =>
                    setCuratorDetails({
                      ...curatorDetails,
                      phone: e.target.value,
                    })
                  }
                  className="input-field"
                />
              ) : (
                <p>{curatorDetails.phone}</p>
              )}

              <label>Bio</label>
              {isEditing ? (
                <textarea
                  value={curatorDetails.bio}
                  onChange={(e) =>
                    setCuratorDetails({
                      ...curatorDetails,
                      bio: e.target.value,
                    })
                  }
                  className="input-field"
                />
              ) : (
                <p>{curatorDetails.bio}</p>
              )}
            </div>

            {isEditing ? (
              <div className="button-group">
                <button className="save-button" onClick={handleSaveChanges}>
                  Save
                </button>
                <button
                  className="cancel-button"
                  onClick={() => setIsEditing(false)}
                >
                  Cancel
                </button>
              </div>
            ) : (
              <button
                className="edit-button"
                onClick={() => setIsEditing(true)}
              >
                Edit Profile
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Right Side Container */}
      <div className="statistics-container">
        <div className="statistics-card">
          <h3>Additional Information</h3>
          <p>Curator-specific content can go here.</p>
        </div>
      </div>
    </div>
  );
};

export default CuratorProfilePage;
