import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const CuratorNavbar = () => {
  const location = useLocation();

  // Function to check if a route is active
  const isActive = (path) => location.pathname === path;

  return (
    <nav style={styles.navbar}>
      <ul style={styles.navList}>
        <li style={styles.navItem}>
          <Link to="/curator" style={isActive("/curator") ? styles.activeNavLink : styles.navLink}>Dashboard</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/curator/exhibition-highlights" style={isActive("/curator/exhibition-highlights") ? styles.activeNavLink : styles.navLink}>Exhibition Highlights</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/curator/visitor-feedback" style={isActive("/curator/visitor-feedback") ? styles.activeNavLink : styles.navLink}>Visitor Feedback</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/curator/artwork-insights" style={isActive("/curator/artwork-insights") ? styles.activeNavLink : styles.navLink}>Artwork Insights</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/curator/gallery-content-management" style={isActive("/curator/gallery-content-management") ? styles.activeNavLink : styles.navLink}>Gallery Content Management</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/curator/manage-artworks1" style={isActive("/curator/manage-artworks1") ? styles.activeNavLink : styles.navLink}>Manage Artworks</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/curator/organize-exhibitions" style={isActive("/curator/organize-exhibitions") ? styles.activeNavLink : styles.navLink}>Organize Exhibition</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/curator/organize-exhibitions" style={isActive("/curator/organize-exhibitions") ? styles.activeNavLink : styles.navLink}>Organize Exhibition</Link>
        </li>
      </ul>
    </nav>
  );
};

const styles = {
  navbar: {
    backgroundColor: '#333',
    padding: '10px 20px',
    color: '#fff',
    borderBottom: '2px solid #444',
  },
  navList: {
    listStyle: 'none',
    display: 'flex',
    justifyContent: 'space-around',
    margin: 0,
    padding: 0,
  },
  navItem: {
    margin: '0 15px',
  },
  navLink: {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '16px',
    fontWeight: '500',
    transition: 'color 0.3s',
  },
  activeNavLink: {
    color: '#ffcc00', // Only change color for active link
    fontSize: '16px',
    fontWeight: '600',
  },
};

export default CuratorNavbar;
