import React, { useEffect, useState } from 'react';
import '../styles/CartPage.css';

export default function CartPage() {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [userId, setUserId] = useState(null); // Store the logged-in user's ID

  // On component mount, check for userId and load cart
  useEffect(() => {
    const storedUserId = localStorage.getItem('userId'); // Fetch the userId from localStorage
    if (storedUserId) {
      setUserId(storedUserId);
      loadCartItems(storedUserId); // Load cart for the specific user
    } else {
      setUserId(null); // User is logged out
      setCartItems([]); // Clear cart if user is logged out
      setLoading(false); // No need to load anything
    }
  }, []);

  // Load cart items for the specific user
  const loadCartItems = (userId) => {
    const savedCart = JSON.parse(localStorage.getItem(`cartItems_${userId}`)) || [];
    fetchArtworkDetails(savedCart);
  };

  // Fetch detailed artwork info (including image URL) from backend
  const fetchArtworkDetails = async (savedCart) => {
    try {
      const detailedItems = await Promise.all(
        savedCart.map(async (item) => {
          const response = await fetch(`http://localhost:8080/api/artworks/${item.id}`);
          if (response.ok) {
            const data = await response.json();
            return { ...item, imageUrl: data.imagePath }; // Append image URL from backend
          } else {
            console.error('Failed to fetch artwork details');
            return item; // In case of failure, return the item as is
          }
        })
      );
      setCartItems(detailedItems);
    } catch (error) {
      console.error('Error fetching artwork details:', error);
    } finally {
      setLoading(false);
    }
  };

  // Remove an item from the cart
  const removeFromCart = (itemId) => {
    const updatedCart = cartItems.filter((item) => item.id !== itemId);
    setCartItems(updatedCart);

    // Save the updated cart to localStorage for the specific user
    if (userId) {
      localStorage.setItem(`cartItems_${userId}`, JSON.stringify(updatedCart));
    }
  };

  if (!userId) {
    return <p style={{ textAlign: 'center' }}>You are logged out. Please log in to view your cart.</p>;
  }

  if (loading) {
    return <p style={{ textAlign: 'center' }}>Loading your cart...</p>;
  }

  if (cartItems.length === 0) {
    return <p style={{ textAlign: 'center' }}>Your cart is empty.</p>;
  }

  return (
    <div>
      <h1 style={{ textAlign: 'center' }}>Your Cart</h1>
      <div className="cart-items">
        {cartItems.map((item) => (
          <div key={item.id} className="cart-item">
            <div className="cart-item-image-container">
              {/* Display image using the URL fetched from backend */}
              <img
                src={`http://localhost:8080${item.imageUrl}`}
                alt={item.artist}
                className="cart-item-image"
              />
            </div>
            <div className="cart-item-details">
              <h2>{item.artist}</h2>
              <p>Cost: {item.cost}</p>
              <button onClick={() => removeFromCart(item.id)}>Remove from Cart</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
