import React, { useState } from 'react';
import axios from 'axios';  // Make sure to install axios: npm install axios
import '../styles/Login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('VISITOR');  // Default role selection
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();  // Prevent the form from reloading the page

    try {
      // Sending login request to the backend with username, password, and role
      const response = await axios.post('http://localhost:8080/login', {
        username,
        password,
        role,  // Send the selected role to the backend
      });

      // Assuming the backend sends back the JWT token and confirms the role
      const { jwt: token } = response.data;

      // Store the JWT token in localStorage
      localStorage.setItem('token', token);

      // Redirect the user based on their role
      switch (role) {
        case 'ADMIN':
          window.location.href = '/admin';
          break;
        case 'ARTIST':
          window.location.href = '/artist';
          break;
        case 'VISITOR':
          window.location.href = '/';
          break;
        case 'CURATOR':
          window.location.href = '/curator';
          break;
        default:
          window.location.href = '/';  // Default fallback page
          break;
      }
    } catch (error) {
      // If there was an error during login, display an error message
      setErrorMessage('Invalid username or password');
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleLogin}>
        <div>
          <h2 style={{ color: 'white', fontSize: '30px', textAlign: 'center' }}>Login</h2>
          <label style={{ color: 'white', fontSize: '15px' }}>Username</label>
          <input
            type="text"
            style={{ backgroundColor: 'white', color: 'black' }}
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label style={{ color: 'white', fontSize: '15px' }}>Password</label>
          <input
            type="password"
            style={{ backgroundColor: 'white', color: 'black' }}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label style={{ color: 'white', fontSize: '15px' }}>Role</label>
          <select
            style={{ backgroundColor: 'white', color: 'black' }}
            value={role}
            onChange={(e) => setRole(e.target.value)}
            required
          >
            <option value="VISITOR">Visitor</option>
            <option value="ADMIN">Admin</option>
            <option value="ARTIST">Artist</option>
            <option value="CURATOR">Curator</option>
          </select>
        </div>
        <div className="login-button">
          <button type="submit">
            <span style={{ fontWeight: 'bold' }}>LOGIN</span>
          </button>

          {/* Display error message if login fails */}
          {errorMessage && <p className="error-message">{errorMessage}</p>}
        </div>
        <p style={{ marginTop: '10px', color: 'white' }}>
          Don't have an account? <a href="/register" style={{ color: 'lightblue' }}>Create one here</a>
        </p>
      </form>
    </div>
  );
};

export default Login;
