import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBCard,
  MDBCardText,
  MDBCardBody,
  MDBCardImage,
  MDBTypography,
} from 'mdb-react-ui-kit';

const ArtistProfilePage = () => {
  const [artistDetails, setArtistDetails] = useState({
    name: '',
    email: '',
    username: '',
    role: '',
    imagePath: '',
  });
  const [newImage, setNewImage] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const artistId = localStorage.getItem('artistId'); // Fetch artistId from localStorage

  // Fetch artist details on component mount
  useEffect(() => {
    if (!artistId) {
      setErrorMessage('Artist not logged in.');
      return;
    }

    // Fetch artist details from API
    axios
      .get(`http://localhost:8080/api/artists/${artistId}`)
      .then((response) => {
        setArtistDetails(response.data);
      })
      .catch((error) => {
        setErrorMessage('Failed to fetch artist details.');
        console.error(error);
      });
  }, [artistId]);

  // Handle image change when selecting a new file
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewImage(file); // Store the selected file in the state
    }
  };

  // Handle profile update
  const handleUpdateProfile = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', artistDetails.name);
    formData.append('email', artistDetails.email);
    formData.append('role', artistDetails.role);
    if (newImage) formData.append('image', newImage); // Add the new image if it's selected

    try {
      // Update the profile with the new image and other details
      const response = await axios.put(
        `http://localhost:8080/api/artists/update/${artistId}`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      if (response.status === 200) {
        setSuccessMessage('Artist profile updated successfully!');
        fetchUpdatedProfile(); // Refresh profile data after update
      } else {
        setErrorMessage('Failed to update profile.');
      }
    } catch (error) {
      setErrorMessage('Failed to update profile.');
      console.error(error.response ? error.response.data : error.message);
    }
  };

  // Fetch updated profile data after successful update
  const fetchUpdatedProfile = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/artists/${artistId}`
      );
      setArtistDetails(response.data);
    } catch (error) {
      setErrorMessage('Failed to fetch updated profile.');
      console.error(error);
    }
  };

  return (
    <section className="vh-100" style={{ backgroundColor: '#f4f5f7' }}>
      <MDBContainer className="py-5 h-100">
        <MDBRow className="justify-content-center align-items-center h-100">
          <MDBCol lg="6" className="mb-4 mb-lg-0">
            <MDBCard className="mb-3" style={{ borderRadius: '.5rem' }}>
              <MDBRow className="g-0">
                {/* Profile Image */}
                <MDBCol
                  md="4"
                  className="gradient-custom text-center text-white"
                  style={{
                    borderTopLeftRadius: '.5rem',
                    borderBottomLeftRadius: '.5rem',
                  }}
                >
                  <MDBCardImage
                    src={
                      artistDetails.imagePath
                        ? `http://localhost:8080${artistDetails.imagePath}?${new Date().getTime()}`
                        : '/default-avatar.jpg'
                    }
                    alt="Avatar"
                    className="my-5"
                    style={{ width: '80px', height: '80px', borderRadius: '50%' }}
                    fluid
                  />
                  <MDBTypography tag="h5">{artistDetails.name}</MDBTypography>
                  <MDBCardText>{artistDetails.role}</MDBCardText>
                </MDBCol>

                {/* Profile Info */}
                <MDBCol md="8">
                  <MDBCardBody className="p-4">
                    {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
                    {successMessage && (
                      <p style={{ color: 'green' }}>{successMessage}</p>
                    )}
                    <form onSubmit={handleUpdateProfile}>
                      <div className="mb-3">
                        <label htmlFor="name">Name</label>
                        <input
                          type="text"
                          id="name"
                          value={artistDetails.name}
                          onChange={(e) =>
                            setArtistDetails({
                              ...artistDetails,
                              name: e.target.value,
                            })
                          }
                          className="form-control"
                        />
                      </div>
                      <div className="mb-3">
                        <label htmlFor="email">Email</label>
                        <input
                          type="email"
                          id="email"
                          value={artistDetails.email}
                          onChange={(e) =>
                            setArtistDetails({
                              ...artistDetails,
                              email: e.target.value,
                            })
                          }
                          className="form-control"
                        />
                      </div>
                      <div className="mb-3">
                        <label htmlFor="role">Role</label>
                        <input
                          type="text"
                          id="role"
                          value={artistDetails.role}
                          onChange={(e) =>
                            setArtistDetails({
                              ...artistDetails,
                              role: e.target.value,
                            })
                          }
                          className="form-control"
                        />
                      </div>
                      <div className="mb-3">
                        <label htmlFor="image">Profile Image</label>
                        <input
                          type="file"
                          id="image"
                          onChange={handleImageChange}
                          className="form-control"
                        />
                      </div>
                      <button type="submit" className="btn btn-primary">
                        Update Profile
                      </button>
                    </form>
                  </MDBCardBody>
                </MDBCol>
              </MDBRow>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
};

export default ArtistProfilePage;
