import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const ArtistNavbar = () => {
  const [artistId, setArtistId] = useState(null);

  // Fetch artist data from the backend
  useEffect(() => {
    const fetchArtistData = async () => {
      try {
        const response = await fetch('/api/artist/id'); // Replace with the correct API endpoint
        if (response.ok) {
          const data = await response.json();
          setArtistId(data.id); // Assuming the backend returns an `id` field for the artist
        } else {
          console.error('Failed to fetch artist data');
        }
      } catch (error) {
        console.error('Error fetching artist data:', error);
      }
    };

    fetchArtistData();
  }, []);

  if (!artistId) {
    return <div>Loading...</div>; // Display a loading message while the artist ID is being fetched
  }

  return (
    <nav style={styles.navbar}>
      <ul style={styles.navList}>
        <li style={styles.navItem}>
          <Link to={`/artist/${artistId}`} style={styles.navLink}>Dashboard</Link>
        </li>
        <li style={styles.navItem}>
          <Link to={`/artist/${artistId}/my-artworks`} style={styles.navLink}>My Artworks</Link>
        </li>
        <li style={styles.navItem}>
          <Link to={`/artist/${artistId}/addartwork`} style={styles.navLink}>Upload Artwork</Link>
        </li>
        <li style={styles.navItem}>
          <Link to={`/artist/${artistId}/profile`} style={styles.navLink}>Profile</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/login" style={styles.navLink}>Logout</Link>
        </li>
      </ul>
    </nav>
  );
};

const styles = {
  navbar: {
    backgroundColor: '#333',
    padding: '15px',
    borderBottom: '2px solid #444',
  },
  navList: {
    listStyle: 'none',
    display: 'flex',
    justifyContent: 'space-around',
    margin: 0,
    padding: 0,
  },
  navItem: {
    margin: '0 10px',
  },
  navLink: {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '16px',
    fontWeight: '500',
    transition: 'color 0.3s, transform 0.2s',
  },
  navLinkHover: {
    color: '#ffcc00',
  }
};

export default ArtistNavbar;
