import React, { useState } from 'react';

const ExhibitionHighlights = () => {
  const [highlight, setHighlight] = useState('');
  const [schedule, setSchedule] = useState('');

  const handleSave = () => {
    console.log('Highlight saved:', highlight, schedule);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ fontSize: '24px', marginBottom: '15px' }}>Exhibition and Artist Highlights</h1>
      
      <label style={{ display: 'block', margin: '10px 0 5px', fontWeight: 'bold' }}>Highlight Text</label>
      <textarea 
        value={highlight} 
        onChange={(e) => setHighlight(e.target.value)} 
        style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ccc', borderRadius: '4px' }}
      />

      <label style={{ display: 'block', margin: '10px 0 5px', fontWeight: 'bold' }}>Schedule Date</label>
      <input 
        type="date" 
        value={schedule} 
        onChange={(e) => setSchedule(e.target.value)} 
        style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ccc', borderRadius: '4px' }}
      />

      <button 
        onClick={handleSave} 
        style={{ padding: '10px 15px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
      >
        Save Highlight
      </button>
    </div>
  );
};

export default ExhibitionHighlights;
