import React, { useEffect, useState } from 'react';
import '../styles/Home.css';
import img1 from '../static/art1.jpg';  
import img2 from '../static/art2.jpg';  
import img3 from '../static/god3.jpg';  

// New arrival images
import newArrival1 from '../static/god1.jpg';
import newArrival2 from '../static/god2.jpg';
import newArrival3 from '../static/paint2.jpg';

const Home = () => {
    const [current, setCurrent] = useState(0);
    const [activeIndex, setActiveIndex] = useState(0);

    const items = [
        img1,   
        img2,    
        img3,    
    ];

    const newArrivals = [
        { img: newArrival1, artist: 'Artist: John Doe' },
        { img: newArrival2, artist: 'Artist: Jane Smith' },
        { img: newArrival3, artist: 'Artist: Alice Johnson' }
    ];

    // Main image slider
    useEffect(() => {
        const interval = setInterval(() => {
            setCurrent((prev) => (prev + 1) % items.length);
        }, 3000); 
        return () => clearInterval(interval);
    }, [items.length]);

    // New arrival slider
    useEffect(() => {
        const interval = setInterval(() => {
            setActiveIndex((prev) => (prev + 1) % newArrivals.length);
        }, 5000); // Slide every 5 seconds
        return () => clearInterval(interval);
    }, [newArrivals.length]);

    return (
        <div>
            {/* Navbar */}
            <nav className="navbar">
                <div className="navbar-brand" style={{ color: 'white' }}>Art Gallery</div>
                <div className="navbar-links">
                    <a href="/">Home</a>
                    <a href="/artworks">Artworks</a>
                    <a href="/add-artwork">Add Artwork</a>
                    <a href="/login">Login</a>
                    <a href="/register">Register</a>
                </div>
            </nav>

            {/* Main Image Carousel */}
            <div className="carousel">
                {items.map((item, index) => (
                    <div
                        className="carousel-item"
                        style={{ opacity: current === index ? 1 : 0 }}
                        key={index}
                    >
                        <img src={item} alt={`Artwork ${index + 1}`} />
                    </div>
                ))}
            </div>

            <h1 style={{ textAlign: 'center' }}>Welcome to the Art Gallery</h1>
            <p style={{ textAlign: 'center' }}>Please Login or Register to view the artworks.</p>

            {/* New Arrivals Section */}
            <section className="new-arrivals">
                <h2>New Arrivals</h2>
                <div className="slider-container">
                    <div className="slider">
                        {newArrivals.map((item, index) => (
                            <div
                                className="slide"
                                key={index}
                                style={{
                                    opacity: activeIndex === index ? 1 : 0,
                                    transform: `translateX(-${activeIndex * 100}%)`,
                                }}
                            >
                                <div className="image-wrapper">
                                    <img
                                        src={item.img}
                                        alt={`New Arrival ${index + 1}`}
                                        className="carousel-image"
                                    />
                                    <p className="artist-name">{item.artist}</p>
                                </div>
                                <p>Product {index + 1}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </section>
        </div>
    );
};

export default Home;
