import React, { useState, useEffect } from 'react';

const ManageExhibitions = () => {
  const [exhibitions, setExhibitions] = useState([]); // State to store exhibitions
  const [editMode, setEditMode] = useState(false); // State to toggle edit mode
  const [selectedExhibition, setSelectedExhibition] = useState(null); // Currently selected exhibition for editing

  // Fetch exhibitions when the component mounts
  useEffect(() => {
    fetch('http://localhost:8080/api/exhibitions') // Replace with your backend API URL
      .then((response) => response.json())
      .then((data) => setExhibitions(data))
      .catch((error) => console.error('Error fetching exhibitions:', error));
  }, []);

  // Handle updating an exhibition
  const handleUpdate = async () => {
    if (!selectedExhibition) return;

    try {
      const response = await fetch(`http://localhost:8080/api/exhibitions/${selectedExhibition.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(selectedExhibition),
      });

      if (response.ok) {
        const updatedExhibition = await response.json();
        setExhibitions((prev) =>
          prev.map((exhibition) =>
            exhibition.id === updatedExhibition.id ? updatedExhibition : exhibition
          )
        );
        setEditMode(false);
        setSelectedExhibition(null);
      } else {
        console.error('Failed to update exhibition.');
      }
    } catch (error) {
      console.error('Error updating exhibition:', error);
    }
  };

  // Handle editing an exhibition
  const startEditing = (exhibition) => {
    setSelectedExhibition({ ...exhibition });
    setEditMode(true);
  };

  // Handle input changes in the edit form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSelectedExhibition((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ fontSize: '24px', marginBottom: '15px' }}>Manage Exhibitions</h1>

      {/* List exhibitions */}
      <ul style={{ listStyleType: 'none', padding: '0' }}>
        {exhibitions.map((exhibition) => (
          <li
            key={exhibition.id}
            style={{
              padding: '8px',
              border: '1px solid #ddd',
              borderRadius: '4px',
              marginBottom: '10px',
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
            }}
          >
            <div>
              <strong>{exhibition.title}</strong> - {exhibition.dates} @ {exhibition.place}
            </div>
            <button
              onClick={() => startEditing(exhibition)}
              style={{
                padding: '5px 10px',
                backgroundColor: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              Edit
            </button>
          </li>
        ))}
      </ul>

      {/* Edit exhibition form */}
      {editMode && selectedExhibition && (
        <div
          style={{
            marginTop: '20px',
            padding: '15px',
            border: '1px solid #ccc',
            borderRadius: '4px',
            backgroundColor: '#f9f9f9',
          }}
        >
          <h2 style={{ fontSize: '20px', marginBottom: '15px' }}>Edit Exhibition</h2>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Title</label>
          <input
            type="text"
            name="title"
            value={selectedExhibition.title}
            onChange={handleInputChange}
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '15px',
              border: '1px solid #ccc',
              borderRadius: '4px',
            }}
          />

          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Description</label>
          <textarea
            name="description"
            value={selectedExhibition.description}
            onChange={handleInputChange}
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '15px',
              border: '1px solid #ccc',
              borderRadius: '4px',
            }}
          />

          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Dates</label>
          <input
            type="text"
            name="dates"
            value={selectedExhibition.dates}
            onChange={handleInputChange}
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '15px',
              border: '1px solid #ccc',
              borderRadius: '4px',
            }}
          />

          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Place</label>
          <input
            type="text"
            name="place"
            value={selectedExhibition.place}
            onChange={handleInputChange}
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '15px',
              border: '1px solid #ccc',
              borderRadius: '4px',
            }}
          />

          <button
            onClick={handleUpdate}
            style={{
              padding: '10px 15px',
              backgroundColor: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
            }}
          >
            Save Changes
          </button>
        </div>
      )}
    </div>
  );
};

export default ManageExhibitions;
