import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthService from './AuthService';
import '../styles/Login.css'; // Import your custom styles here

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    AuthService.login(username, password)
      .then((response) => {
        // Save the token or user data in local storage
        localStorage.setItem('user', JSON.stringify(response.data));
        navigate('/artworks');  // Redirect to artworks page after login
      })
      .catch((error) => {
        setErrorMessage('Invalid credentials');
      });
  };

  return (
    <div className="login-container">
     


      <form onSubmit={handleLogin}>
        <div>
        <h2 style={{ color: 'white', fontSize: '30px', textAlign:'center'}}>Login</h2>
          <label style={{ color: 'white', fontSize: '15px' }}>Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label style={{ color: 'white', fontSize: '15px' }}>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {/* Updated submit button */}
        <button type="submit" >
          <span>SUBMIT</span>
        </button>
        {errorMessage && <p>{errorMessage}</p>}
        <p style={{ marginTop: '10px', color:'white'}}>Don't have an account? <a href="/register">Create one here</a></p>
      </form>
      
    </div>
    
  );
};

export default Login;
