import React, { useState } from 'react';
import { Route, Link, Routes, Navigate } from 'react-router-dom';
import '../styles/AdminDashboard.css';
import ManageUsers from './ManageUsers';
import ManageArtworks from './ManageArtworks';
import SiteSettings from './SiteSettings';

const AdminHomePage = () => {
  const [isSidebarExpanded, setIsSidebarExpanded] = useState(false);

  return (
    <div className="admin-dashboard">
      {/* Sidebar */}
      
      {/* Main Content */}
      <div 
        className="dashboard-content" 
        style={{ marginLeft: isSidebarExpanded ? '200px' : '60px' }}
      >
        <h1>Admin Dashboard</h1>
        <p>Welcome to the Admin Dashboard where you can manage users, artworks, and permissions.</p>

        <div className="page-content">
          <Routes>
            <Route path="/" element={<HomeContent />} /> {/* Default admin home */}
            <Route path="manage-users" element={<ManageUsers />} />
            <Route path="manage-artworks" element={<ManageArtworks />} />
            <Route path="site-settings" element={<SiteSettings />} />
            <Route path="*" element={<Navigate to="/admin" />} /> {/* Fallback to home */}
          </Routes>
        </div>
      </div>
    </div>
  );
};

const HomeContent = () => (
  <div>
    <h2>Welcome, Admin!</h2>
    <p>This is your admin home page where you can find an overview of the dashboard functionalities.</p>
  </div>
);

export default AdminHomePage;
