import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/Profile.css';

const VisitorProfile = () => {
  const [userData, setUserData] = useState(null);
  const [profileImage, setProfileImage] = useState(null);
  const [message, setMessage] = useState('');

  const userId = localStorage.getItem('userId'); // Retrieve userId from localStorage

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/users/${userId}`);
        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
  }, [userId]);

  const handleProfileImageUpload = async (e) => {
    e.preventDefault();
    if (!profileImage) return;

    const formData = new FormData();
    formData.append('image', profileImage);

    try {
      const response = await axios.put(`http://localhost:8080/api/users/${userId}/upload-image`, formData);
      setMessage('Profile image updated successfully!');
      setUserData((prevData) => ({
        ...prevData,
        profileImageUrl: response.data.profileImageUrl,
      }));
    } catch (error) {
      console.error('Error uploading profile image:', error);
      setMessage('Failed to update profile image.');
    }
  };

  return (
    <div className="profile-container">
      <h1>Your Profile</h1>
      {userData ? (
        <div className="profile-card">
          <img
            src={userData.profileImageUrl || '/default-profile.png'}
            alt="Profile"
            className="profile-image"
          />
          <h2>{userData.name}</h2>
          <p><strong>Email:</strong> {userData.email}</p>
          <p><strong>Role:</strong> {userData.role}</p>

          <form onSubmit={handleProfileImageUpload} className="upload-form">
            <label htmlFor="profile-image">Update Profile Image:</label>
            <input
              type="file"
              id="profile-image"
              accept="image/*"
              onChange={(e) => setProfileImage(e.target.files[0])}
            />
            <button type="submit">Upload</button>
          </form>
          {message && <p className="message">{message}</p>}
        </div>
      ) : (
        <p>Loading profile...</p>
      )}
    </div>
  );
};

export default VisitorProfile;
