import React, { useState } from 'react';
import '../styles/Home.css';
import img1 from '../static/art1.jpg';  
import img2 from '../static/art2.jpg';  
import img3 from '../static/god3.jpg';  
import img4 from '../static/art4.jpg';  // Add more images as needed
import img5 from '../static/art5.jpg';  // Add more images as needed

const Home = () => {
    const items = [
        { src: img1, artist: "Sujata Achrekar" },   
        { src: img2, artist: "John Doe" },    
        { src: img3, artist: "Jane Smith" },    
        { src: img4, artist: "Michael Artis" },
        { src: img5, artist: "Rina Kaur" },
    ];

    const [current, setCurrent] = useState(0);

    const scrollLeft = () => {
        if (current > 0) {
            setCurrent(current - 1);
        }
    };

    const scrollRight = () => {
        if (current < items.length - 4) { // Adjust 4 based on number of items to show at once
            setCurrent(current + 1);
        }
    };

    return (
        <div>
            {/* Navbar */}
            <nav className="navbar">
                <div className="navbar-brand" style={{ color: 'white' }}>Art Gallery</div>
                <div className="navbar-links">
                    <a href="/">Home</a>
                    <a href="/artworks">Artworks</a>
                    <a href="/add-artwork">Add Artwork</a>
                    <a href="/login">Login</a>
                    <a href="/register">Register</a>
                </div>
            </nav>

            {/* New Arrivals Section */}
            <h1 style={{ textAlign: 'center' }}>Welcome to the Art Gallery</h1>
            <p style={{ textAlign: 'center' }}>
                Explore a stunning collection of contemporary and classical artworks. 
                Login or Register to curate your own gallery, save favorites, and discover the artists behind each masterpiece.
            </p>

            {/* New Arrivals Section */}
            <div className="new-arrivals-section">
                <h2 style={{ textAlign: 'center' }}>New Arrivals</h2>
                <div className="new-arrivals-carousel-wrapper">
                    <button className="scroll-btn left" onClick={scrollLeft}>{'<'}</button>
                    <div className="new-arrivals-carousel" style={{ transform: `translateX(-${current * 300}px)` }}>
                        {items.map((item, index) => (
                            <div className="new-arrival-item" key={index}>
                                <img src={item.src} alt={`New Arrival ${index + 1}`} />
                                <div className="artist-name">{item.artist}</div>
                            </div>
                        ))}
                    </div>
                    <button className="scroll-btn right" onClick={scrollRight}>{'>'}</button>
                </div>
            </div>
        </div>
    );
};

export default Home;
