import React, { useState } from 'react';
import '../styles/AddArtwork.css'; // Ensure your styles include the body-background

export default function AddArtwork() {
  const [title, setTitle] = useState('');
  const [artist, setArtist] = useState('');
  const [cost, setCost] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title || !artist || !cost || !description || !image) {
      setMessage('All fields are required');
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('artist', artist);
    formData.append('cost', cost);
    formData.append('description', description);
    formData.append('image', image);

    try {
      const response = await fetch('/api/artworks', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        setMessage('Artwork added successfully!');
        setTitle('');
        setArtist('');
        setCost('');
        setDescription('');
        setImage(null);
      } else {
        setMessage('Failed to add artwork. Try again.');
      }
    } catch (error) {
      console.error('Error adding artwork:', error);
      setMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div>
      {/* Background Image */}
      <div className="body-background"></div>
      
      {/* Form Container */}
      <div className="add-artwork">
        <h1>Add New Artwork</h1>

        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="title">Title:</label>
            <input
              type="text"
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="artist">Artist Name:</label>
            <input
              type="text"
              id="artist"
              value={artist}
              onChange={(e) => setArtist(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="cost">Cost:</label>
            <input
              type="number"
              id="cost"
              value={cost}
              onChange={(e) => setCost(e.target.value)}
              required
            />
          </div>
          <div>
            <label htmlFor="description">Description:</label>
            <textarea
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              rows="4"
              required
            />
          </div>
          <div>
            <label htmlFor="image">Upload Image:</label>
            <input
              type="file"
              id="image"
              accept="image/*"
              onChange={(e) => setImage(e.target.files[0])}
              required
            />
          </div>
          <button type="submit">Add Artwork</button>
        </form>
        {message && <p className="message">{message}</p>}
      </div>
    </div>
  );
}
