import React, { useState } from 'react';

const GalleryContentManagement = () => {
  const [layout, setLayout] = useState('Grid');

  const handleLayoutChange = (e) => setLayout(e.target.value);

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ fontSize: '24px', marginBottom: '15px' }}>Gallery Content Management</h1>
      
      <label style={{ display: 'block', margin: '10px 0 5px', fontWeight: 'bold' }}>Gallery Layout</label>
      <select 
        value={layout} 
        onChange={handleLayoutChange} 
        style={{ width: '100%', padding: '10px', marginBottom: '15px', border: '1px solid #ccc', borderRadius: '4px' }}
      >
        <option value="Grid">Grid</option>
        <option value="Carousel">Carousel</option>
        <option value="List">List</option>
      </select>
    </div>
  );
};

export default GalleryContentManagement;
