import React, { useState } from 'react';
import axios from 'axios';
import '../styles/Register.css';

const Register = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('VISITOR'); // Default role
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validatePassword = (password) => {
    const passwordRegex = /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    return passwordRegex.test(password);
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    // Validation
    if (!validateEmail(email)) {
      setErrorMessage('Invalid email format');
      return;
    }

    if (!validatePassword(password)) {
      setErrorMessage(
        'Password must be at least 8 characters, include one uppercase letter, one number, and one special character.'
      );
      return;
    }

    // Send data to the backend using Axios
    try {
      const response = await axios.post('http://localhost:8080/api/submit', {
        name,
        email,
        username,
        password,
        role
      });

      if (response.status === 200) {
        setSuccessMessage('Registration successful!');
        setErrorMessage('');
        setName('');
        setEmail('');
        setUsername('');
        setPassword('');
        setRole('VISITOR');

        // Redirect based on the role
        switch (role) {
          case 'ADMIN':
            window.location.href = '/admin'; // Redirect to admin page
            break;
          case 'ARTIST':
            window.location.href = '/artist'; // Redirect to artist page
            break;
          case 'VISITOR':
            window.location.href = '/login'; // Redirect to home page
            break;
          case 'CURATOR':
            window.location.href = '/curator'; // Redirect to curator page
            break;
          default:
            window.location.href = '/login'; // Default fallback to home page
            break;
        }
      } else {
        setErrorMessage('Failed to register user');
        setSuccessMessage('');
      }
    } catch (error) {
      setErrorMessage('An error occurred: ' + (error.response ? error.response.data.message : error.message));
    }
  };

  return (
    <div className="register-container">
      <h2>Register</h2>
      <form onSubmit={handleRegister}>
        <div className="input-group">
          <label>Name</label>
          <input
            type="text"
            style={{ backgroundColor: 'white', color: 'black' }}
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label>Email</label>
          <input
            type="email"
            style={{ backgroundColor: 'white', color: 'black' }}
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label>Username</label>
          <input
            type="text"
            style={{ backgroundColor: 'white', color: 'black' }}
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label>Password</label>
          <input
            type="password"
            style={{ backgroundColor: 'white', color: 'black' }}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="input-group">
          <label>Role</label>
          <select
            style={{ backgroundColor: 'white', color: 'black' }}
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="ADMIN">Admin</option>
            <option value="ARTIST">Artist</option>
            <option value="VISITOR">Visitor</option>
            <option value="CURATOR">Curator</option>
          </select>
        </div>
        <button type="submit">
          <span style={{ fontWeight: 'bold' }}>REGISTER</span>
        </button>
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
        <p style={{ marginTop: '10px', color: 'white' }}>
          Already have an account?{' '}
          <a href="/login" style={{ color: 'lightblue' }}>
            Login here
          </a>
        </p>
      </form>
    </div>
  );
};

export default Register;
