import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/MyArtworks.css';

export default function MyArtworks() {
  const [artworks, setArtworks] = useState([]);  // All fetched artworks
  const [filteredArtworks, setFilteredArtworks] = useState([]); // Artworks filtered by the logged-in artist
  const [errorMessage, setErrorMessage] = useState('');
  const [userDetails, setUserDetails] = useState(null);

  const userId = localStorage.getItem('userId'); // Get logged-in user ID from localStorage

  // Fetch user details to get the artist's name
  useEffect(() => {
    if (!userId) {
      setErrorMessage('User not logged in.');
      return;
    }

    // Fetch user details to identify the logged-in artist
    axios
      .get(`http://localhost:8080/api/users/${userId}`)
      .then((response) => {
        setUserDetails(response.data);  // Store user details
        fetchArtworks(response.data.username);  // Fetch artworks for the logged-in artist
      })
      .catch((error) => {
        setErrorMessage('Failed to fetch user details.');
        console.error(error);
      });
  }, [userId]);

  // Fetch all artworks from the backend and filter them based on the logged-in artist
  const fetchArtworks = (artistName) => {
    axios
      .get('http://localhost:8080/api/artworks')
      .then((response) => {
        const allArtworks = response.data;
        
        // Filter artworks based on the logged-in artist's name
        const artistArtworks = allArtworks.filter(
          (artwork) => artwork.artist === artistName
        );

        setArtworks(allArtworks);  // Set all artworks (if needed for other logic)
        setFilteredArtworks(artistArtworks);  // Set filtered artworks belonging to the logged-in artist
      })
      .catch((error) => {
        setErrorMessage('Failed to fetch artworks.');
        console.error(error);
      });
  };

  return (
    <div className="my-artworks">
      <h1>My Artworks</h1>
      {errorMessage && <p>{errorMessage}</p>}
      <div className="artworks-container">
        {filteredArtworks.length > 0 ? (
          filteredArtworks.map((artwork) => (
            <div key={artwork.id} className="artwork-card">
              <img
                src={artwork.imagePath ? `/uploads/${artwork.imagePath}` : '/default-image.jpg'}
                alt={artwork.artist}
                onError={(e) => (e.target.src = '/default-image.jpg')}  // Fallback for missing images
              />
              <h2>{artwork.title || 'Untitled'}</h2>
              <p><strong>Artist:</strong> {artwork.artist || 'Unknown'}</p>
              <p><strong>Cost:</strong> ${artwork.cost || 'N/A'}</p>
              <p><strong>Description:</strong> {artwork.description || 'No description available'}</p>
              <p><strong>Added On:</strong> {artwork.timestamp ? new Date(artwork.timestamp).toLocaleString() : 'Unknown'}</p>
              <div className="actions">
                <button className="edit-button">Edit</button>
                <button className="delete-button">Delete</button>
              </div>
            </div>
          ))
        ) : (
          <p>No artworks found for this artist.</p>
        )}
      </div>
    </div>
  );
}
