import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBCard,
  MDBCardText,
  MDBCardBody,
  MDBCardImage,
  MDBTypography,
} from 'mdb-react-ui-kit';

const ArtistProfilePage = () => {
  const [artistDetails, setArtistDetails] = useState({
    name: '',
    email: '',
    username: '',
    role: '',
    imagePath: '',
  });
  const [newImage, setNewImage] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [editableField, setEditableField] = useState(null); // Track which field is being edited

  const artistId = localStorage.getItem('artistId'); // Fetch artistId from localStorage

  // Fetch artist details on component mount
  useEffect(() => {
    if (!artistId) {
      setErrorMessage('Artist not logged in.');
      return;
    }

    // Fetch artist details from API
    axios
      .get(`http://localhost:8080/api/artists/${artistId}`)
      .then((response) => {
        setArtistDetails(response.data);
      })
      .catch((error) => {
        setErrorMessage('Failed to fetch artist details.');
        console.error(error);
      });
  }, [artistId]);

  // Handle image change when selecting a new file
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewImage(file); // Store the selected file in the state
    }
  };

  // Handle profile update for a specific field
  const handleUpdateField = async (field) => {
    const formData = new FormData();
    formData.append(field, artistDetails[field]);
    if (newImage && field === 'imagePath') formData.append('image', newImage);

    try {
      // Update the profile with the new value
      const response = await axios.put(
        `http://localhost:8080/api/artists/update/${artistId}`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      if (response.status === 200) {
        setSuccessMessage(`${field} updated successfully!`);
        setEditableField(null); // Exit edit mode for the field
        fetchUpdatedProfile(); // Refresh profile data after update
      } else {
        setErrorMessage(`Failed to update ${field}.`);
      }
    } catch (error) {
      setErrorMessage(`Failed to update ${field}.`);
      console.error(error.response ? error.response.data : error.message);
    }
  };

  // Fetch updated profile data after successful update
  const fetchUpdatedProfile = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/artists/${artistId}`
      );
      setArtistDetails(response.data);
    } catch (error) {
      setErrorMessage('Failed to fetch updated profile.');
      console.error(error);
    }
  };

  return (
    <section className="vh-100" style={{ backgroundColor: '#f4f5f7' }}>
      <MDBContainer className="py-5 h-100">
        <MDBRow className="justify-content-center align-items-center h-100">
          <MDBCol lg="6" className="mb-4 mb-lg-0">
            <MDBCard className="mb-3" style={{ borderRadius: '.5rem' }}>
              <MDBRow className="g-0">
                {/* Profile Image */}
                <MDBCol
                  md="4"
                  className="gradient-custom text-center text-white"
                  style={{
                    borderTopLeftRadius: '.5rem',
                    borderBottomLeftRadius: '.5rem',
                  }}
                >
                  <MDBCardImage
                    src={
                      artistDetails.imagePath
                        ? `http://localhost:8080${artistDetails.imagePath}?${new Date().getTime()}`
                        : '/default-avatar.jpg'
                    }
                    alt="Avatar"
                    className="my-5"
                    style={{ width: '80px', height: '80px', borderRadius: '50%' }}
                    fluid
                  />
                  {editableField === 'imagePath' ? (
                    <>
                      <input
                        type="file"
                        onChange={handleImageChange}
                        className="form-control mt-2"
                      />
                      <button
                        className="btn btn-primary mt-2"
                        onClick={() => handleUpdateField('imagePath')}
                      >
                        Save Image
                      </button>
                    </>
                  ) : (
                    <button
                      className="btn btn-outline-light mt-2"
                      onClick={() => setEditableField('imagePath')}
                    >
                      Edit Image
                    </button>
                  )}
                  <MDBTypography tag="h5">{artistDetails.name}</MDBTypography>
                  <MDBCardText>{artistDetails.role}</MDBCardText>
                </MDBCol>

                {/* Profile Info */}
                <MDBCol md="8">
                  <MDBCardBody className="p-4">
                    {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
                    {successMessage && (
                      <p style={{ color: 'green' }}>{successMessage}</p>
                    )}
                    <div className="mb-3">
                      <label>Name</label>
                      {editableField === 'name' ? (
                        <>
                          <input
                            type="text"
                            value={artistDetails.name}
                            onChange={(e) =>
                              setArtistDetails({
                                ...artistDetails,
                                name: e.target.value,
                              })
                            }
                            className="form-control"
                          />
                          <button
                            className="btn btn-primary mt-2"
                            onClick={() => handleUpdateField('name')}
                          >
                            Save
                          </button>
                        </>
                      ) : (
                        <>
                          <p>{artistDetails.name}</p>
                          <button
                            className="btn btn-outline-primary"
                            onClick={() => setEditableField('name')}
                          >
                            Edit
                          </button>
                        </>
                      )}
                    </div>
                    <div className="mb-3">
                      <label>Email</label>
                      {editableField === 'email' ? (
                        <>
                          <input
                            type="email"
                            value={artistDetails.email}
                            onChange={(e) =>
                              setArtistDetails({
                                ...artistDetails,
                                email: e.target.value,
                              })
                            }
                            className="form-control"
                          />
                          <button
                            className="btn btn-primary mt-2"
                            onClick={() => handleUpdateField('email')}
                          >
                            Save
                          </button>
                        </>
                      ) : (
                        <>
                          <p>{artistDetails.email}</p>
                          <button
                            className="btn btn-outline-primary"
                            onClick={() => setEditableField('email')}
                          >
                            Edit
                          </button>
                        </>
                      )}
                    </div>
                    <div className="mb-3">
                      <label>Role</label>
                      {editableField === 'role' ? (
                        <>
                          <input
                            type="text"
                            value={artistDetails.role}
                            onChange={(e) =>
                              setArtistDetails({
                                ...artistDetails,
                                role: e.target.value,
                              })
                            }
                            className="form-control"
                          />
                          <button
                            className="btn btn-primary mt-2"
                            onClick={() => handleUpdateField('role')}
                          >
                            Save
                          </button>
                        </>
                      ) : (
                        <>
                          <p>{artistDetails.role}</p>
                          <button
                            className="btn btn-outline-primary"
                            onClick={() => setEditableField('role')}
                          >
                            Edit
                          </button>
                        </>
                      )}
                    </div>
                  </MDBCardBody>
                </MDBCol>
              </MDBRow>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </section>
  );
};

export default ArtistProfilePage;
