import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Navbar.css';

const Navbar = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('authToken'); // Token for authentication
    const userId = localStorage.getItem('userId'); // Retrieve user ID from localStorage
    setIsAuthenticated(!!token);
    setUserId(userId);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('authToken'); // Remove auth token
    localStorage.removeItem('userId'); // Remove user ID
    setIsAuthenticated(false);
    navigate('/'); // Redirect to home page
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand" style={{ color: 'white' }}>Art Gallery</div>
      <div className="navbar-links">
        <Link to="/">Home</Link>
        <Link to="/artworks">Artworks</Link>
        <Link to={userId ? `/visitor/${userId}/cart` : "/cart"}>Cart</Link>
        <Link to="/about">About</Link>
        {isAuthenticated ? (
          <>
            <Link to={`/visitor/${userId}`}>Profile</Link>
            <button
              onClick={handleLogout}
              style={{ background: 'none', border: 'none', color: 'white', cursor: 'pointer' }}
            >
              Logout
            </button>
          </>
        ) : (
          <Link to="/login">Login/Register</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
