import React from 'react';
import { Link } from 'react-router-dom';

const ArtistNavbar = () => {
  // Get userId from localStorage
  const userId = localStorage.getItem('userId');

  if (!userId) {
    // Handle the case where userId is not found (user might not be logged in)
    return <div>Please log in first.</div>;
  }

  return (
    <nav style={styles.navbar}>
      <ul style={styles.navList}>
        <li style={styles.navItem}>
          <Link to={`/artist/${userId}`} style={styles.navLink}>Dashboard</Link>
        </li>
        <li style={styles.navItem}>
          <Link to={`/artist/${userId}/myartworks`} style={styles.navLink}>My Artworks</Link>
        </li>
        <li style={styles.navItem}>
          <Link to={`/artist/${userId}/addartwork`} style={styles.navLink}>Upload Artwork</Link>
        </li>
        <li style={styles.navItem}>
          <Link to={`/artist/${userId}/profile`} style={styles.navLink}>Profile</Link>
        </li>
        <li style={styles.navItem}>
          <Link to="/login" style={styles.navLink}>Logout</Link>
        </li>
      </ul>
    </nav>
  );
};

const styles = {
  navbar: {
    backgroundColor: '#333',
    padding: '15px',
    borderBottom: '2px solid #444',
  },
  navList: {
    listStyle: 'none',
    display: 'flex',
    justifyContent: 'space-around',
    margin: 0,
    padding: 0,
  },
  navItem: {
    margin: '0 10px',
  },
  navLink: {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '16px',
    fontWeight: '500',
    transition: 'color 0.3s, transform 0.2s',
  },
  navLinkHover: {
    color: '#ffcc00',
  }
};

export default ArtistNavbar;
