import React, { useState } from 'react';

const artworks = [
  { id: 1, title: 'Artwork Title', artist: 'Artist Name', price: 100, img: 'https://placehold.co/300' },
  { id: 2, title: 'Artwork Title 2', artist: 'Artist Name 2', price: 150, img: 'https://placehold.co/301' },
  { id: 3, title: 'Artwork Title 3', artist: 'Artist Name 3', price: 120, img: 'https://placehold.co/302' },
];

const ArtworkList = () => {
  const [quantities, setQuantities] = useState(artworks.map(() => 1)); // Initialize quantities

  const handleQuantityChange = (index, increment) => {
    setQuantities(prev => {
      const newQuantities = [...prev];
      newQuantities[index] = Math.max(1, newQuantities[index] + increment); // Ensure quantity is at least 1
      return newQuantities;
    });
  };

  return (
    <div className="bg-background text-primary-foreground min-h-screen flex flex-col items-center justify-center">
      <div className="max-w-4xl grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 p-4">
        {artworks.map((artwork, index) => (
          <div key={artwork.id} className="bg-card shadow-lg rounded-lg overflow-hidden flex flex-col relative">
            <img src={artwork.img} alt={artwork.title} className="w-full h-48 object-cover" />
            <div className="p-4 flex flex-col justify-between flex-1">
              <div>
                <h3 className="text-lg font-semibold">{artwork.title}</h3>
                <p className="text-sm text-muted-foreground opacity-0 artist-name">{artwork.artist}</p>
                <p className="text-base mt-2">Price: ${artwork.price}</p>
              </div>
              <div className="flex justify-between items-center mt-4">
                <button className="bg-primary text-primary-foreground px-4 py-2 rounded-lg">Add to Cart</button>
                <div className="flex items-center">
                  <button
                    className="bg-secondary text-secondary-foreground px-2 py-1 rounded-l-lg"
                    onClick={() => handleQuantityChange(index, -1)}
                  >
                    -
                  </button>
                  <span className="bg-input text-primary-foreground px-4 py-2">{quantities[index]}</span>
                  <button
                    className="bg-secondary text-secondary-foreground px-2 py-1 rounded-r-lg"
                    onClick={() => handleQuantityChange(index, 1)}
                  >
                    +
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      <script>
        {`
          const cards = document.querySelectorAll('.bg-card');
          cards.forEach(card => {
            card.addEventListener('mouseenter', () => {
              const artistName = card.querySelector('.artist-name');
              artistName.style.opacity = '1';
            });
            card.addEventListener('mouseleave', () => {
              const artistName = card.querySelector('.artist-name');
              artistName.style.opacity = '0';
            });
          });
        `}
      </script>
    </div>
  );
};

export default ArtworkList;
